import React, { useState } from 'react';
import { createCommerce } from '../services/commerceService';

const CommerceForm = () => {
  const [formData, setFormData] = useState({
    RUC: '',
    NombreComercial: '',
    RazonSocial: '',
    Direccion: '',
    Telefono: '',
    NombreContacto: '',
    TipoEstablecimiento: 'Restaurante',
    ImagenLogotipo: null
  });

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: files ? files[0] : value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const formDataToSend = new FormData();
      for (const key in formData) {
        formDataToSend.append(key, formData[key]);
      }
      await createCommerce(formDataToSend);
      alert('Commerce created successfully!');
    } catch (error) {
      console.error('Error creating commerce:', error);
      alert('Failed to create commerce');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" name="RUC" value={formData.RUC} onChange={handleChange} placeholder="RUC" required />
      <input type="text" name="NombreComercial" value={formData.NombreComercial} onChange={handleChange} placeholder="Nombre Comercial" required />
      <input type="text" name="RazonSocial" value={formData.RazonSocial} onChange={handleChange} placeholder="Razón Social" required />
      <input type="text" name="Direccion" value={formData.Direccion} onChange={handleChange} placeholder="Dirección" required />
      <input type="text" name="Telefono" value={formData.Telefono} onChange={handleChange} placeholder="Teléfono" required />
      <input type="text" name="NombreContacto" value={formData.NombreContacto} onChange={handleChange} placeholder="Nombre de Contacto" required />
      <select name="TipoEstablecimiento" value={formData.TipoEstablecimiento} onChange={handleChange}>
        <option value="Restaurante">Restaurante</option>
        <option value="Transporte">Transporte</option>
        <option value="Hotel">Hotel</option>
      </select>
      <input type="file" name="ImagenLogotipo" onChange={handleChange} />
      <button type="submit">Crear Comercio</button>
    </form>
  );
};

export default CommerceForm;
